gel:script.sql:query:
select INITIALS, CODE, PARTITION_CODE, NAME
from odf_ca_stproj_project
Where id = ${vStudioProjectID}

core:otherwise.sql:query:
select element_id from ODF_CA_STPROJ_ELEMENT
Where odf_parent_id = ${vStudioProjectID}
AND element_type='Process'

select XLAT_SPREADSHEET 
from odf_ca_stproj_project
Where id = ${vStudioProjectID}
